import React from 'react';
import styled from 'styled-components';
import {Box, Button, Grid} from '@material-ui/core'
import s from '../../styles/styles.module.css'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      height: 140,
      width: 100,
    },
    control: {
      padding: theme.spacing(2),
    },
  }));


  export default function Hero() {
    const [spacing, setSpacing] = React.useState(2);
    const classes = useStyles();
    const Text = styled.p`
    color: white;
    text-align: left;
    font-size: 1em;
    margin: 1em;
    padding: 0.25em 1em;
`
   return (
            <Box p={2} color="seconday">
            <Text>
              <h1 className={s.header} style={{textAlign: 'center'}}>
              The world's first marketplace for trained artificial intelligence models
              </h1>
              <Grid container alignItems="center" justify="center">
                  <Grid item ></Grid>
              </Grid>
              <Grid item xs={12}>
        <Grid container justify="center" spacing={spacing}>
          {[0, 1, 2].map((value) => (
            <Grid key={value} item>
              <Paper className={classes.paper} />
            </Grid>
          ))}
        </Grid>
      </Grid>
              <p ><strong>Choose a data source</strong></p>
              <span >Streaming video, image, text, or unstructured</span>
              <p ><strong>Choose a model</strong></p>
              <p ><strong>Choose how to get results</strong></p>
              <span>Dashboard, streaming JSON, or API</span>
              </Text>
              <Button size="large" variant="contained" color="secondary" href="/auth">
                <strong>Get Started</strong>
              </Button>
            </Box>
     )
    }
