import React from 'react';
import styled from 'styled-components';
import {Box, Button} from '@material-ui/core'
import s from '../../styles/styles.module.css'


const Hero = () =>  (
      <HeaderWrapper>

            <Box p={2}>
            <Art>

            </Art>

            <Text>
              <h1 className={s.white} >
              A marketplace for artificial intelligence
              </h1>
              <br />
              <p className={s.white}></p>
              <br/>
              <p className={s.white}><strong>Choose a data source</strong></p>
              <span className={s.white}>Streaming video, image, text, or unstructured</span>
              <br />
              <br/>
              <br/>
              <p className={s.white}><strong>Choose a model</strong></p>
              <br />
              <br/>
              <p className={s.white}><strong>Choose how to get results</strong></p>
              <span className={s.white}>Dashboard, streaming JSON, or API</span>
              <br />
              <br/>
              <br/>
              <br/>
              <Button size="large" className={s.whiteButton} href="#contact">
                Contact Us
              </Button>
              <br />
              {/* <p className={s.white}>
                <StyledExternalLink href="https://www.zeptron.com.au/products">
                  Products &nbsp;&#x2794;
                </StyledExternalLink>
              </p> */}
            </Text>
            </Box>
      </HeaderWrapper>
    )


const HeaderWrapper = styled.header`
  background-color: ${props => props.theme.palette.canvasColor};
  padding-top: 96px;

  @media (max-width: ${props => props.theme.screen.md}) {
    padding-top: 128px;
  }
`;

const Art = styled.figure`
  width: 100%;
  margin: 0;

  > div {
    width: 120%;
    margin-bottom: -4.5%;

    @media (max-width: ${props => props.theme.screen.md}) {
      width: 100%;
    }
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-items: center;
  grid-gap: 64px;

  @media (max-width: ${props => props.theme.screen.md}) {
    grid-template-columns: 1fr;
    grid-gap: 80px;

    > ${Art} {
      order: 2;
    }
  }
`;

const Text = styled.div`
  justify-self: center;

  @media (max-width: ${props => props.theme.screen.md}) {
    justify-self: start;
  }
`;



export default Hero;
