import React from 'react';
import styled from 'styled-components';
import {Box, Button, Grid} from '@material-ui/core'
import s from '../../styles/styles.module.css'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Spacer from 'react-spacer'
import Data from '../../assets/animations/data'
import Brain from '../../assets/animations/brain'
import Results from '../../assets/animations/results'



const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      height: 200,
      width: 200,
      padding: theme.spacing(2),
      textAlign: 'center'

    },
    control: {
      padding: theme.spacing(4),
    },
  }));


  export default function Hero() {
    const [spacing, setSpacing] = React.useState(8);
    const classes = useStyles();
    const Text = styled.p`
    color: white;
    text-align: left;
    font-size: 1em;
    margin: 1em;
    padding: 0.25em 1em;
    `
    const BoxHeading = styled.h3`
    text-transform: uppercase;
    `
   return (

            <Box bgcolor="primary.dark" color="primary.contrastText" p={2} >

                <Spacer height="40px"/>
               <Grid container alignItems="center" justify="center">
                   <Grid item md={8}>
                   <h1  className={s.header} style={{textAlign: 'center'}}>
                Join the world's first marketplace for trained artificial intelligence models
                </h1>
                   </Grid>
               </Grid>

                <Spacer height="10px"/>
                <Box>
                    <Grid container justify="center" spacing={spacing}>
                    <Grid item md={8}>
                    <Grid container justify="center" spacing={spacing}>

                    <Grid item md={3}>
                    <Data/>
                    <BoxHeading>Connect data </BoxHeading>
                    </Grid>
                    <Grid item md={3}>
                    <Brain/>
                    <BoxHeading>Choose a model</BoxHeading>
                    </Grid>
                    <Grid item md={3}>
                    <Results/>
                    <BoxHeading>Get results</BoxHeading>
                    </Grid>
                    </Grid>
                    </Grid> 
                    </Grid>
                    </Box>


            <Spacer height="80px"/>

            <Box>
            <Grid container justify="center"    >
                <Grid item xs={12}>
                <Button size="large" variant="contained" color="secondary" href="/auth">
                <strong>Start Now for FREE</strong>
              </Button>
              </Grid>
              </Grid>
              <Spacer height="50px"/>
              </Box>
            </Box>


     )
    }
