import React from 'react';
import styled from 'styled-components';
import {Box, Button, Grid} from '@material-ui/core'
import s from '../../styles/styles.module.css'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Spacer from 'react-spacer'
import VideocamIcon from '@material-ui/icons/Videocam';
import FormatListNumberedIcon from '@material-ui/icons/FormatListNumbered';
import PieChartIcon from '@material-ui/icons/PieChart';
import DynamicFeedIcon from '@material-ui/icons/DynamicFeed';
import NotificationsActiveIcon from '@material-ui/icons/NotificationsActive';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,

    },
    paper: {
        height: 250,
        width: 250,
      padding: theme.spacing(2),
      textAlign: 'center'

    },
    control: {
      padding: theme.spacing(2),
    },
  }));


  export default function Hero() {
    const [spacing, setSpacing] = React.useState(2);
    const classes = useStyles();
    const Text = styled.p`
    color: white;
    text-align: left;
    font-size: 1em;
    margin: 1em;
    padding: 0.25em 1em;
    `
    const BoxHeading = styled.h3`
    text-transform: uppercase;
    text-align: center;
    margin-bottom: 5px;

    `
    const Notes = styled.p`
    text-align: left;
    margin: 0px 0px 5px 0px;
    `
   return (
    <Box p={2} bgcolor="primary.dark" color="primary.contrastText" >
         <Grid container justify="center" spacing={spacing}>
            <Grid item md={8} sm={10} xs={12}>
        <Grid container justify="center" spacing={spacing}>
            <Grid item xs={12}>
            <Spacer height="40px"/>
            <h2 className={s.header} style={{textAlign: 'center'}}>
            Use our platform to get instant results
            </h2>
            <p className={s.subheader}>Start and stop models easily, schedule run times, and set up dashboards, counting algorithms, and notification systems through our user-friendly UI </p>
            <Spacer height="40px"/>
            </Grid> 
            <Grid item>
                <Paper color="secondary" className={`${classes.paper}`} >
                <VideocamIcon fontSize="large"/>
                <hr/>
                <BoxHeading>5 video data sources</BoxHeading>
                <Notes>Our proprietary streaming service stream for free from Raspberry Pi, Mac, PC or Linux</Notes>
                </Paper>
            </Grid>
            <Grid item>
                <Paper color="secondary" className={`${classes.paper}`} >
                <FormatListNumberedIcon fontSize="large"/>
                <hr/>
                <BoxHeading>Built-in quick actions</BoxHeading>
                </Paper>
            </Grid>
            <Grid item>
                <Paper color="secondary" className={`${classes.paper}`} >
                <NotificationsActiveIcon fontSize="large"/>
                <hr/>
                <BoxHeading>Custom notifications</BoxHeading>
                </Paper>
            </Grid>
            <Grid item>
              <Paper color="secondary" className={`${classes.paper}`} >
                <PieChartIcon color="secondary" fontSize="large"/>
                <hr/>
              <BoxHeading>Analytics Dashboard</BoxHeading>
              </Paper>
            </Grid>
            <Grid item>
                <Paper color="secondary" className={`${classes.paper}`} >
                <DynamicFeedIcon fontSize="large"/>
                <hr/>
                <BoxHeading>JSON & CSV Data Feeds</BoxHeading>
                </Paper>
            </Grid>
            <Grid item>
                <Paper color="secondary" className={`${classes.paper}`} >
                <MonetizationOnIcon fontSize="large"/>
                <hr/>
                <BoxHeading>No Monthly Fee</BoxHeading>
                </Paper>
            </Grid>
            </Grid> 
        </Grid> 
        </Grid>
    </Box>

)
}
